public class Area {     
	public static void main(String[] args) {
		// TODO Auto-generated method stub
     
       int length=34,
           breadth=55;
           
       double area=(0.5)*length*breadth;    
       
       System.out.println("Base of triangle: "+breadth+" cm");
       System.out.println("height of triangle: "+length+" cm");
       System.out.println("Area of triangle: "+area+" cm");
	}

}
